create table especificacoes (
  codigo_especificacoes serial primary key,
  quant_quartos integer not null,
  quant_banheiros integer not null,
  has_garagem boolean not null,
  has_fogao boolean not null,
  has_geladeira boolean not null,
  has_tv boolean not null,
  has_internet boolean not null,
  has_microondas boolean not null
);

